package de.jojoob.dh;

import java.math.BigInteger;

public class DHParams {

	/**
	 * RFC 3562
	 * 4096-bit MODP Group id 16
	 *
	 * Generator g of RFC 3562 MODP Groups is always 2.
	 */
	public static BigInteger RFC3562_16_p = new BigInteger(
			"",
			16);

	/**
	 * RFC 5114
	 * 2048-bit MODP Group with 256-bit Prime Order Subgroup
	 */
	public static BigInteger RFC5114_2028_256_p = new BigInteger(
			"",
			16);
	/**
	 * Group order of generator g is
	 * 8CF83642 A709A097 B4479976 40129DA2 99B1A47D 1EB3750 A308B0FE 64F5FBD3
	 */
	public static BigInteger RFC5114_2048_256_g = new BigInteger(
			"",
			16);

	private DHParams() {}
}
